import React from 'react';
import styled from 'styled-components';


const Label = styled.label`
  background: red;
  display: block;
  padding: 1rem;
`;

const Input = styled.input`
  &:checked + ${Label} {
    background: blue;
  }
`;

const CheckBox = () => (
  <div>
    <Input id="input" type="checkbox" />
    <Label for="input" />
  </div>
);

export default CheckBox
