const initialState = {
    adults: 0,
    children: 0

  }
function occupancyOfRoom(room = ''){
     return function occupancy(state = {}, action) {
        switch (action.type){
            case `NUMBER_ADULTS_${room}`:
                return {
                    ...state,
                    adults: action.selectValue,
                    room: action.room
                }
            case `NUMBER_CHILDREN_${room}`:
                return {
                    ...state,
                    children: action.selectValue,
                    room: action.room
                }                
            
          default:
              return state
     }
    }
  }

  export default occupancyOfRoom
