import { connect } from 'react-redux'
import styled from 'styled-components'
import Room1 from './room1'
import Room from './room'
import { check2, check3, check4 } from '../actions'

const PageContainer = styled.div`
  display: flex;
  flex-direction: row;
`

const Home = ({ dispatch, theState }) => {
  console.log(theState.check);

  return (
    <PageContainer>
      <Room1 />
      <Room room={'Room 2'} name={'box2'} handleClick={() => dispatch(check2("box2"))} checked={theState.check.check2}/>
      <Room room={'Room 3'} name={'box3'} handleClick={() => dispatch(check3("box3"))} checked={theState.check.check3}/>
      <Room room={'Room 4'} name={'box4'} handleClick={() => dispatch(check4("box4"))} checked={theState.check.check4}/>
    </PageContainer>
  )
}

const mapStateToProps = (state) => ({
  theState : state
})


export default connect(
  mapStateToProps
)(Home)
