import { createStore } from 'redux';
import { rootReducer } from './reducers';
import middleware from './middleware';
import { loadState } from './localStorage';


export function initializeStore (initialState) {
  return createStore(
    rootReducer,
    initialState,
    loadState,
    middleware,

  )
}
