import { createStore } from 'redux';
import { rootReducer } from './reducers';
import middleware from './middleware';
import { loadState } from './localStorage';


export function initializeStore () {
  const peristedState = loadState()
  return createStore(
    rootReducer,
    peristedState,


    middleware
  )
}
